insert into Receta (nombre, tiempo, proceso, imagen) values ('Flammkuchen', 35, 'Para preparar Flammkuchen, comienza aplanando la masa de pizza entre dos trozos de papel pergamino hasta que tenga un grosor de 1-2 mm, luego colócala en una bandeja para hornear y precalienta el horno. Distribuye la crema agria uniformemente sobre la masa, luego corta la cebolla y el tocino en trozos pequeños y distribúyelos uniformemente sobre la crema. Hornea en el horno precalentado durante 10 minutos hasta que esté ligeramente dorado. Finalmente, sazona con sal y pimienta al gusto.', 'https://www.kimieatsglutenfree.com/wp-content/uploads/2020/04/UpgradedThinCrustYoghurtPizzaAmmanfordProcessed7-2048x1365.jpg');
